#METHODES
def print_number_handles(listing_journalists) #Calcul le nombre d'handles dans le tableau d'entrée
    num = listing_journalists.length
    puts "Il y a #{num} handles dans ce tableau !"
    return num
end

def print_min_handles(listing_journalists) #Affiche l'handle le plus petit
    shandle = listing_journalists.min_by(&:length)
    puts "L'handle le plus court est : #{shandle} !"
    return shandle
end

def delete_the_at(listing_journalists) #Supprime tous les '@' du tableau d'entrée
    listing_journalists_without_at = listing_journalists.map {|word| word.gsub('@', '')}
    return listing_journalists_without_at
end

def print_number_handles_5char(listing_journalists, num, list_without_at) #Calcul le nombre d'handles à 5 caractères sans compter le @
    listing_handle_5char = []
    num.times do |i|
        if list_without_at[i].length() == 5 then
            listing_handle_5char << list_without_at[i]
        end
    end
    puts "Il y a #{listing_handle_5char.length} handles de 5 caractères sans le '@' !"
    return listing_handle_5char
end

def number_handles_begining_with_cap(listing_journalists, list_without_at, num) #Calcul le nombre d'handles qui commence par une majuscule
    list_f_letter = []
    num.times do |i|
        list_f_letter << list_without_at[i].chars.first
    end
    puts "Le nombre d'handles qui commence par une maj est de : #{list_f_letter.count{|e| e =~ /^[A-Z]/}}"
end


def alphabetical_sort(list_without_at) #Trie par ordre alphabétique
    list_sort_by_alphabet = list_without_at.sort # Ascending sort!
    #puts "Voici la liste trié par ordre alphabétique : #{list_sort_by_alphabet}"
end

def length_sort(listing_journalists) #Trie par longueur des handles
    list_sort_by_length = listing_journalists.sort_by {|x| x.length}
    #puts "Voici la liste trié par taille des handles : #{list_sort_by_length}"
end

def print_index_of_epenser(listing_journalists) #Affiche l'index de l'handle "@epenser"
    index_of_epenser = listing_journalists.index("@epenser")
    puts "L'index de @epenser est : #{index_of_epenser}"
end

def sort_by_similar_length(listing_journalists, num) #Créer un nouveau tableau qui affiche une répartition des handle selon leur taille
    mhandle = listing_journalists.max_by(&:length).length + 1
    listing_into_length = []
    listing_into_length_compact = []
    counter=0
    num.times do |i| #On transforme le tableau d'entrée en un tableau de nombre de caractère
        count = 0
        listing_journalists[i].each_char { |c| count += c.length }
        #puts count
        listing_into_length[i] = count
    end
    mhandle.times do |e| #Première boucle
        num.times do |g| #Deuxième boucle

            if listing_into_length[g] == e then
                counter = counter + 1
            end
        end
        listing_into_length_compact << counter
        counter = 0
    end
    puts listing_into_length_compact
end

#CALL PROCESS
def perform(listing_journalists)
    number_handles = print_number_handles(listing_journalists) #Récupère dans la variable number_handles le nombre d'handle en faisant appel à la fonction print_number_handles
    list_without_at = delete_the_at(listing_journalists) #Récupère dans la variable list_without_at la liste d'entrée sans les @ en faisant appel à la fonction delete_the_at
    min_handles = print_min_handles(listing_journalists) #Récupère dans la variable min_handles l'handle le plus petit de la liste d'entrée à l'aide de la fonction print_min_handles

    print_number_handles_5char(listing_journalists, number_handles, list_without_at) #Appel la fonction print_number_handles_5char qui calcul le nombre d'handle composé de 5 caractères
    number_handles_begining_with_cap(listing_journalists, list_without_at, number_handles) #Appel la fonction number_handles_begining_with_cap qui va compter le nombre d'handles commançant par une majuscule
    alphabetical_sort(list_without_at) #Appel la fonction alphabetical_sort qui va trier la liste d'entrée par ordre alphabétique
    length_sort(listing_journalists) #Appel la fonction length_sort qui va trier la liste d'entrée par longueur d'handles
    print_index_of_epenser(listing_journalists)
    sort_by_similar_length(listing_journalists, number_handles)
end

perform(listing_journalists)